// Normalization for SerpAPI Google News Light -> news_results items

function normalizeUrl(urlStr) {
  try {
    if (!urlStr) return '';
    // remove hash
    let base = urlStr.split('#')[0];

    // split path + query
    let path = base;
    let query = '';
    const qIdx = base.indexOf('?');
    if (qIdx >= 0) {
      path = base.slice(0, qIdx);
      query = base.slice(qIdx + 1);
    }

    // lowercase scheme+host
    path = path.replace(/^(https?:\/\/)([^/]+)/i, (_, scheme, host) => scheme + host.toLowerCase());

    // parse & filter query params (no global URL needed)
    const trackers = new Set(['utm_source','utm_medium','utm_campaign','utm_term','utm_content','gclid','fbclid','mc_cid','mc_eid']);
    const params = {};
    if (query) {
      const pairs = query.split('&');
      for (const pair of pairs) {
        if (!pair) continue;
        const [k, v=''] = pair.split('=');
        if (!k) continue;
        const key = decodeURIComponent(k);
        if (trackers.has(key)) continue;
        const val = decodeURIComponent(v);
        (params[key] ||= []).push(val);
      }
    }
    const entries = Object.keys(params).sort().flatMap(k => params[k].map(v => [k, v]));
    const qs = entries.map(([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v)}`).join('&');

    return qs ? `${path}?${qs}` : path;
  } catch (e) {
    return urlStr || '';
  }
}

function simpleHash(str) {
  let hash = 0;
  if (!str || str.length === 0) return hash.toString();
  for (let i = 0; i < str.length; i++) {
    const chr = str.charCodeAt(i);
    hash = ((hash << 5) - hash) + chr;
    hash |= 0;
  }
  return Math.abs(hash).toString();
}

function parseRelativeDate(s) {
  if (!s || typeof s !== 'string') return '';
  const now = new Date();
  const txt = s.trim().toLowerCase();

  if (txt === 'today' || txt === 'just now') return now.toISOString();
  if (txt === 'yesterday') return new Date(now.getTime() - 86400000).toISOString();

  const m = txt.match(/^(\d+)\s+(minute|hour|day|week|month|year)s?\s+ago$/);
  if (m) {
    const n = parseInt(m[1], 10);
    const unit = m[2];
    const msMap = {
      minute: 60 * 1000,
      hour:   60 * 60 * 1000,
      day:    24 * 60 * 60 * 1000,
      week:   7 * 24 * 60 * 60 * 1000,
      month:  30 * 24 * 60 * 60 * 1000,   // approx
      year:   365 * 24 * 60 * 60 * 1000   // approx
    };
    const ms = msMap[unit] || 0;
    if (ms) return new Date(now.getTime() - n * ms).toISOString();
  }

  // Sometimes SerpAPI can return exact dates (rare). Try parsing.
  const t = Date.parse(s);
  return isNaN(t) ? '' : new Date(t).toISOString();
}

// After "Split Out" (field: news_results). Each incoming item is one article.
return $input.all().map(item => {
  const art = item.json || {};
  const norm = normalizeUrl(art.link || '');
  const publishedAt = parseRelativeDate(art.date);

  const hashBase = norm || `${art.source || ''}|${art.title || ''}|${publishedAt || art.date || ''}`;
  const id = simpleHash(hashBase);

  return {
    json: {
      id,
      position: art.position ?? null,
      title: art.title || '',
      author: '',                 // not provided by this API
      source_id: '',              // not provided by this API
      source_name: art.source || '',
      description: art.snippet || '',
      url: art.link || '',
      normalized_url: norm,
      urlToImage: art.thumbnail || '',
      publishedAt,                // ISO when derivable; '' otherwise
      date_relative: art.date || ''         // not provided by this API
    }
  };
});

const out = [];

for (const i of items) {
  const parts = i.json.content?.parts;
  if (!parts || !parts[0]?.text) continue;

  const raw = parts[0].text.trim();
  let arr;
  try {
    arr = JSON.parse(raw);        // Gemini returns a JSON array (as text)
  } catch (e) {
    throw new Error("Parse error: " + raw.slice(0, 400));
  }

  for (const obj of arr) {
    // Hoist the arrays you want to split on
    out.push({
      json: {
        id: obj.id,
        // top-level mirrors (so Item Lists can split them)
        countries_impacted: obj.countries_impacted ?? [],
        impacted_nodes: obj.impacted_nodes ?? [],
        risk_types: obj.risk_types ?? [],
        // keep full model payload too for mapping later
        parsedData: obj,
        _raw_model_json: raw
      }
    });
  }
}

return out;

return $input.all().flatMap(({ json }) => {
  const d = json.parsedData || {};

  // 1) Countries list: prefer countries_impacted; fallback to impacted_nodes[].iso2; else [null]
  const countries = Array.isArray(d.countries_impacted) && d.countries_impacted.length > 0
    ? d.countries_impacted
    : (Array.isArray(d.impacted_nodes) && d.impacted_nodes.length > 0
        ? d.impacted_nodes.map(node => node?.iso2 ?? null)
        : [null]);

  // 2) Scores + enums
  const s = d.scores || {};
  const allowed = new Set(['port','canal','strait','factory','rail','airport','border','road','power','telecom','other']);
  const node0 = Array.isArray(d.impacted_nodes) && d.impacted_nodes.length ? d.impacted_nodes[0] : {};
  const nodeName = node0?.name ?? null;
  const nodeTypeRaw = node0?.type ?? null;
  const nodeType = nodeTypeRaw && allowed.has(nodeTypeRaw) ? nodeTypeRaw : (nodeTypeRaw ? 'other' : null);

  // 3) Keys + hygiene
  const eventId = json['Primary Key'] || null;
  const title = (json.Title || '').trim() || null;
  const description = ((json.Description ?? json.Desription) || '').trim() || null;
  const source = json.source_name || json.source?.name || null;
  const publishedAt = json['Published At'] || null;
  const normalizedUrl = json['normalized url'] || null;
  const url = json.url || null;

  // 4) Emit one row per country
  return countries.map(rawIso => {
    const iso2 = typeof rawIso === 'string' && rawIso.trim() ? rawIso.trim().toUpperCase() : null;
    const safeIso = iso2 || 'NA';

    return {
      json: {
        // provenance
        published_at: publishedAt,
        title,
        description,
        source,
        url,
        normalized_url: normalizedUrl,

        // keys
        event_id: eventId,
        row_id: eventId ? `${eventId}|${safeIso}` : null,

        // exploded dimension
        country_iso2: iso2,

        // model fields
        risk_types: Array.isArray(d.risk_types) ? d.risk_types.join(',') : null,
        node_name: nodeName,
        node_type: nodeType,
        scope: d.scope || null,
        economic_importance: d.economic_importance || null,
        trade_dependency: d.trade_dependency || null,
        population_impact_bucket: d.population_impact_bucket || null,
        impact_summary: d.impact_summary || null,

        // scores
        scope_score: s.scope_score ?? null,
        node_importance_score: s.node_importance_score ?? null,
        risk_type_score: s.risk_type_score ?? null,
        trade_dependency_score: s.trade_dependency_score ?? null,
        population_impact_score: s.population_impact_score ?? null,
        suggested_model_score: s.suggested_model_score ?? null,

        // QA
        confidence: d.confidence ?? null,
        is_speculative: !!d.is_speculative,

        // audit
        _raw_model_json: json.content?.parts?.[0]?.text || null
      }
    };
  });
});

