// Next Page (Code) ‚Äî after the SerpAPI HTTP node
// Mode: Run once for all items

const res = items[0]?.json || {};
const hasNext = !!(res.serpapi_pagination && res.serpapi_pagination.next);
if (!hasNext) {
  return []; // stop the loop
}

// Read current paging from response (no URL parsing needed)
const sp = res.search_parameters || {};
const currentStart = Number(sp.start || 0);
const num = Number(sp.num || 10);

// Base params from your Init node (rename "Init" if yours is different)
const init = $items("Init")[0].json;

return [{
  json: {
    // keep your original query parameters
    engine: init.engine || 'google_news_light',
    q: init.q,
    hl: init.hl || 'en',
    gl: init.gl || 'in',
    google_domain: init.google_domain || 'google.com',
    device: init.device || 'desktop',
    no_cache: init.no_cache ?? false,
    "daysSinceLast": init.daysSinceLast,

    // pagination
    num: num,                      // same page size as the response
    start: currentStart + num      // advance the offset
  },
  pairedItem: { item: 0 }
}];

// Inputs: many items, each item.json = { id, title, summary, url, published_at, ... }
  const input = items.map(i => i.json);
const total = input.length;

// Round up so every item is included
const BATCH_SIZE = Math.ceil(total / 10);

// make contiguous batches
const batches = [];
for (let i = 0; i < input.length; i += BATCH_SIZE) {
  const chunk = input.slice(i, i + BATCH_SIZE);
  const batchIndex = Math.floor(i / BATCH_SIZE);
  const lane = batchIndex % 10; // 5 parallel lanes
  batches.push({
    json: {
      batch_id: batchIndex,
      lane,
      items: chunk, // array of articles for this batch
    }
  });
}
return batches;
